package com.suryaenergi.sdm.backendapi.email.template;

import com.suryaenergi.sdm.backendapi.email.EmailTemplate;
import com.suryaenergi.sdm.backendapi.email.EmailContent;
import com.suryaenergi.sdm.backendapi.email.Alignment;

import java.awt.*;

public abstract class AbstractEmailTemplate extends EmailTemplate {
    private static final String LEN_LOGO_BASE64 = "";
    private static final String SEI_LOGO_BASE64 = "";
    private final String subject;
    private boolean pdfCompatible;

    public AbstractEmailTemplate(String subject, boolean pdfCompatible) {
        this.subject = subject;
        this.pdfCompatible = pdfCompatible;
    }

    public void setPdfCompatible(boolean pdfCompatible) {
        this.pdfCompatible = pdfCompatible;
    }

    @Override
    public EmailContent build() {
        if (!pdfCompatible) {
            return col(
                    child(desktopOnly(col(
                            child(
                                    container(
                                            col(
                                                    child(
                                                            col(
                                                                    child(row(
                                                                            child(h1(subject)).width(percent(100)),
                                                                            child().width(px(20)),
                                                                            child(imgNetwork("https://suryaenergi.com/wp-content/uploads/2021/10/m_logo4.png", "Logo SEI")).alignment(Alignment.CENTER_RIGHT).width(px(80)),
                                                                            child().width(px(10)),
                                                                            child(imgNetwork("https://www.len.co.id/wp-content/uploads/2023/05/Logo-Len-Holding-of-DEFEND-ID-Web.png", "Logo LEN")).alignment(Alignment.CENTER_RIGHT).width(px(80)),
                                                                            child().width(percent(100))
                                                                    )),
                                                                    child(buildContent()),
                                                                    child().height(px(30)),
                                                                    // footer
                                                                    child(span(
                                                                            text("PT Surya Energi Indotama"),
                                                                            br(),
                                                                            text("Jl. Soekarno Hatta.439, setelah No.347, Kb. Lega, Kec. Bojongloa Kidul, Kota Bandung, Jawa Barat 40235"),
                                                                            br(),
                                                                            text("Office SEI v2.0")
                                                                    ).style().fontSize(12.0).italic(true).color(Color.gray)).alignment(Alignment.BOTTOM_CENTER)
                                                            )
                                                    ).backgroundColor(Color.white).padding(px(30), px(20))
                                            )
                                    ).style().maxWidth(px(700))
                            ).backgroundColor(Color.darkGray).padding(px(40)).alignment(Alignment.CENTER)
                    ))),
                    child(mobileOnly(col(
                            child(
                                    container(
                                            col(
                                                    child(
                                                            col(
                                                                    child(row(
                                                                            child(imgNetwork("https://suryaenergi.com/wp-content/uploads/2021/10/m_logo4.png", "Logo SEI")).width(px(80)),
                                                                            child().width(px(10)),
                                                                            child(imgNetwork("https://www.len.co.id/wp-content/uploads/2023/05/Logo-Len-Holding-of-DEFEND-ID-Web.png", "Logo LEN")).width(px(80)),
                                                                            child().width(percent(100))
                                                                    )),
                                                                    child(h1(subject)),
                                                                    child(buildContent()),
                                                                    child().height(px(30)),
                                                                    // footer
                                                                    child(span(
                                                                            text("PT Surya Energi Indotama"),
                                                                            br(),
                                                                            text("Jl. Soekarno Hatta.439, setelah No.347, Kb. Lega, Kec. Bojongloa Kidul, Kota Bandung, Jawa Barat 40235"),
                                                                            br(),
                                                                            text("Office SEI v2.0")
                                                                    ).style().fontSize(12.0).italic(true).color(Color.gray)).alignment(Alignment.BOTTOM_CENTER)
                                                            )
                                                    ).backgroundColor(Color.white).padding(px(30), px(20))
                                            )
                                    )
                            ).backgroundColor(Color.darkGray).padding(zero)
                    )))
            ).style().font("Arial").width(percent(100));
        }
        return col(
                child(
                        col(
                                child(row(
                                        child(h1(subject)).width(percent(100)),
                                        child().width(px(20)),
                                        child(imgBase64(SEI_LOGO_BASE64, "Logo SEI")).alignment(Alignment.CENTER_RIGHT).width(px(80)),
                                        child().width(px(10)),
                                        child(imgBase64(LEN_LOGO_BASE64, "Logo LEN")).alignment(Alignment.CENTER_RIGHT).width(px(80)),
                                        child().width(percent(100))
                                )),
                                child(buildContent()),
                                child().height(px(30)),
                                // footer
                                child(span(
                                        text("PT Surya Energi Indotama"),
                                        br(),
                                        text("Jl. Soekarno Hatta.439, setelah No.347, Kb. Lega, Kec. Bojongloa Kidul, Kota Bandung, Jawa Barat 40235"),
                                        br(),
                                        text("Office SEI v2.0")
                                ).style().fontSize(12.0).italic(true).color(Color.gray)).alignment(Alignment.BOTTOM_CENTER)
                        )
                ).backgroundColor(Color.white).padding(px(30), px(20))
        ).style().font("Arial").width(percent(100));
    }

    public boolean isPdfCompatible() {
        return pdfCompatible;
    }

    public abstract EmailContent buildContent();
}
